mapper.insert:
        INSERT INTO MB_SHARE (
            USER_NO
            , TARGET_TYPE
            , TARGET_NO
        ) VALUES (
			#{userNo}
			, #{targetType, typeHandler=com.utime.memoBom.board.typeHandler.EShareTargetTypeTypeHandler} 
            , #{targetNo}
        )

mapper.select:
SELECT SHARE_NO , CAST(UID AS VARCHAR) AS UID , TARGET_TYPE , TARGET_NO FROM MB_SHARE WHERE 1=1
AND UID = #{uid}
AND SHARE_NO = #{shareNo}

SELECT TOPIC_NO AS "targetNo" , NAME AS "text" , CAST(UID AS VARCHAR) AS "uid" FROM MB_TOPIC WHERE 1=1 AND ENABLED = TRUE
AND UID = #{uid}
AND TOPIC_NO = #{topicNo}

SELECT FG.FRAGMENT_NO AS "targetNo" , FG.CONTENT AS "text" , CAST(FG.UID AS VARCHAR) AS "uid" FROM MB_FRAGMENT FG WHERE 1=1 AND FG.IS_DELETED = FALSE
AND FG.UID = #{uid}
AND FG.FRAGMENT_NO = #{fragmentNo}
