mapper.insert:
INSERT INTO MB_TOPIC (
	UPDATE_DATE 
	, OWNER_NO
	, ENABLED 
	, EXTERNAL
	, NAME 
	, NAME_UPPER 
	, DESCRIPTION
	, IMOGI
	, COLOR
	, EMOJI_SET_TYPE
) VALUES (
	CURRENT_TIMESTAMP
	, #{ownerNo}
	, TRUE
	, #{external}
	, #{name}
	, UPPER(#{name})
	, #{description}
	, #{imogi}
	, #{color}
	, #{emojiSetType, typeHandler=com.utime.memoBom.board.typeHandler.EmojiSetTypeHandler}
)

mapper.update:
UPDATE MB_TOPIC SET
	UPDATE_DATE = CURRENT_TIMESTAMP
	, ENABLED = #{enabled}
	, EXTERNAL = #{external}
	, NAME = #{name}
	, NAME_UPPER = UPPER(#{name})
	, DESCRIPTION =  #{description} NULL 
	, IMOGI =  #{imogi} NULL 
	, COLOR =  #{color} NULL 
	, EMOJI_SET_TYPE = #{emojiSetType, typeHandler=com.utime.memoBom.board.typeHandler.EmojiSetTypeHandler}
WHERE TOPIC_NO = #{topicNo}

mapper.select:
SELECT

FROM MB_TOPIC
WHERE 1=1

		AND UID = #{uid}


		AND TOPIC_NO = #{topicNo}

	    SELECT TOPIC_NO
	    FROM MB_TOPIC
        WHERE UID = #{uid}

mapper.insert:
INSERT INTO MB_TOPIC_FOLLOW (
    USER_NO
    , TOPIC_NO
) VALUES (
    #{userNo}
    , #{topicNo}
)

mapper.delete:
DELETE FROM MB_TOPIC_FOLLOW
WHERE USER_NO = #{userNo}
  AND TOPIC_NO = #{topicNo}
